function getTotal(info) {
  let result = 0;
  for (let i = 0; i <= info.length - 1; i++) {
    result += (10 - i) * info[i];
  }
  return result;
}

function backtrack(n, info, total) {
  let remain = n;
  let myTotal = 0;
  let result = [];
  // remainì´ 1ì¼ë•Œê¹Œì§€ ë°˜ë³µ

  for (let i = 0; i < 10; i++) {
    let myShot = info[i] + 1;
    let myScore = 10 - i;
    // ì–¸ì œ ì¶”ê°€í•˜ì§€?
    // ì ë‹¹íˆ ë°°ë¶„ë˜ì–´ì•¼í•¨.
    if (remain <= 0 || total < myTotal) {
      return result;
    }
    console.log(
      "inner for remain : ",
      remain,
      "\n",
      "myShot : ",
      myShot,
      "\n",
      "myScore : ",
      myScore,
      "\n",
      "myTotal : ",
      myTotal,
    );
    if (myTotal + myScore < total) {
      result.push(myShot);
      myTotal += myScore;
      remain = remain - myShot;
    }
  }

  return [-1];
}

function solution(n, info) {
  let infoTotal = getTotal(info);
  let result = backtrack(n, info, infoTotal);

  return result;
}

