function solution(arr, target) {
  let k = new Set();
  for (let i = 0; i < arr.length; i++) {
    k.add(target - arr[i]);
  }

  for (let i = 0; i < arr.length; i++) {
    let comple = target - arr[i];
    if (k.has(arr[i]) && comple > 0 && comple !== arr[i]) {
      return true;
    }
  }
  return false;
}

