/**
 * @param {character[][]} board
 * @return {void} Do not return anything, modify board in-place instead.
 */
var solve = function (board) {
  let rSize = board.length,
    cSize = board[0].length;
  let visited = Array.from({ length: rSize }, () => Array(cSize).fill(false));
  let offset = [
    [1, 0],
    [0, 1],
    [-1, 0],
    [0, -1],
  ];

  // dfs t/f ë°˜í™˜. të©´ Xë¡œ ë°”ê¾¼ë‹¤, fë©´ ë°”ê¾¸ì§€ ì•ŠëŠ”ë‹¤.
  function dfs([r, c]) {
    if (visited[r][c]) return false;
    visited[r][c] = true;

    const isCurrentEdge =
      r === 0 || c === 0 || r === rSize - 1 || c === cSize - 1;

    // ì¸ì ‘ì´ Xì¸ ê²½ìš°, ì—°ê²°ëœ ëª¨ë“  Oê°€ Xë¼ë©´ ë°”ê¿”ì•¼í•¨
    if (board[r][c] === "X") return true;
    // ë˜ëŠ” Oê°€ ê°€ì¥ ìë¦¬ì— ìˆë‹¤ë©´ Xë¡œ ë°”ê¿€ ìˆ˜ ì—†ìŒ
    if (board[r][c] === "O" && isCurrentEdge) return false;

    // í˜„ì¬ ë…¸ë“œ = "O" ì¸ì ‘ ë…¸ë“œì— ë”°ë¼ ìê¸° ìì‹ ì´ ë°”ë€œ
    // 1. ì¸ì ‘ ë…¸ë“œë¥¼ ëª¨ë‘ ë°©ë¬¸í•˜ê³ ,
    // 2. ìê¸° ìì‹ ì˜ ë°”ê¾¸ëŠ” ì—¬ì§€ë¥¼ ì •í•¨.

    // ì¸ì ‘ ë…¸ë“œ ë°©ë¬¸ ì „ì— í˜„ì¬ Oë¥¼ Xë¡œ ë°”ê¾¸ëŠ” ë¡œì§ì´ í•„ìš”í•˜ë‹¤
    // O

    /**
        ëª©ì : dfsê°€ tfë¥¼ ë°˜í™˜í•˜ì—¬ ì¸ì ‘ ë…¸ë“œ ë°©ë¬¸í•œ ê²°ê³¼ê°’ì´ t/fë¥¼ ë°˜í™˜í•˜ê²Œ í•œë‹¤.
        ì¡°ê±´:
        - ëª¨ë“  ì¸ì ‘ì´ Xì´ë©´? í˜„ì¬ ë…¸ë“œë¥¼ Xë¡œ ë°”ê¾¼ë‹¤
        - ëª¨ë“  ì¸ì ‘ì´ Xê°€ ì•„ë‹ˆë©´? Oì¸ ë…¸ë“œë¡œ ì´ë™í•œë‹¤
        - í˜„ì¬ ë…¸ë“œì˜ r, cê°€ 0ì´ë‚˜ r,cSize-1ì´ë©´? ë³€í™˜í•˜ì§€ ì•ŠëŠ”ë‹¤ - ìœ„ì—ì„œ false ë³€í™˜
         **/

    for (let [or, oc] of offset) {
