var findMedianSortedArrays = function (nums1, nums2) {
  let merged = [...nums1, ...nums2].sort((a, b) => a - b);
  let size = merged.length,
    isEven = size % 2 === 0;
  let pointer = isEven ? Math.floor(size / 2) - 1 : Math.floor(size / 2);

