        wait.delete(orders[i] + next);
        head++;
      }
    } else if (orders[i] === balls[tail]) {
      result.push(orders[i]);
      if (wait.has(orders[i] + prev)) {
        while (wait.has(orders[i] + prev)) {
          result.push(orders[i] + prev);
          wait.delete(orders[i] + prev);
          tail--;
        }
      }
      tail--;
    } else {
      wait.add(orders[i]);
    }
  }
  return result;
};

