var longestPalindrome = function (s) {
  if (s.length < 2) return s; // í•œ ê¸€ìë©´ ê·¸ëŒ€ë¡œ ë°˜í™˜

  let start = 0,
    maxLength = 1;

  function expandAroundCenter(left, right) {
    while (left >= 0 && right < s.length && s[left] === s[right]) {
      left--;
      right++;
    }
    return [left + 1, right - 1]; // íŒ°ë¦°ë“œë¡¬ì˜ ì‹œì‘ê³¼ ë ì¸ë±ìŠ¤ ë°˜í™˜
  }

  for (let i = 0; i < s.length; i++) {
    // í™€ìˆ˜ ê¸¸ì´ íŒ°ë¦°ë“œë¡¬
    let [l1, r1] = expandAroundCenter(i, i);
    // ì§ìˆ˜ ê¸¸ì´ íŒ°ë¦°ë“œë¡¬
    let [l2, r2] = expandAroundCenter(i, i + 1);

    if (r1 - l1 + 1 > maxLength) {
      start = l1;
      maxLength = r1 - l1 + 1;
    }
    if (r2 - l2 + 1 > maxLength) {
      start = l2;
      maxLength = r2 - l2 + 1;
    }
  }

  return s.slice(start, start + maxLength);
};
